import React, { useState, useMemo } from "react";
import {
  PieChart,
  Pie,
  Cell,
  ResponsiveContainer,
  Tooltip as RechartsTooltip,
  Legend,
} from "recharts";
import {
  PlusCircle,
  Trash2,
  Moon,
  Sun,
  TrendingUp,
  LayoutDashboard,
  Settings,
  Calendar,
} from "lucide-react";
import "./App.css";

const INITIAL_SUBSCRIPTIONS = [
  {
    id: 1,
    name: "Netflix",
    price: 43.0,
    currency: "PLN",
    category: "Rozrywka",
    nextPayment: "2025-11-15",
  },
  {
    id: 2,
    name: "Spotify",
    price: 19.99,
    currency: "PLN",
    category: "Muzyka",
    nextPayment: "2025-11-20",
  },
  {
    id: 3,
    name: "Adobe CC",
    price: 140.0,
    currency: "PLN",
    category: "Praca",
    nextPayment: "2025-11-28",
  },
  {
    id: 4,
    name: "Siłownia",
    price: 120.0,
    currency: "PLN",
    category: "Zdrowie",
    nextPayment: "2025-11-05",
  },
];

const COLORS = ["#6366f1", "#ec4899", "#10b981", "#f59e0b", "#8b5cf6"];

// Komponent dla widoku kalendarza
const CalendarView = ({ subscriptions }) => (
  <div className="calendar-view-container">
    <Calendar size={48} className="calendar-icon" style={{ margin: "0 auto", display: "block" }} />
    <h3 style={{ textAlign: "center", marginTop: "1rem", fontSize: "1.5rem", fontWeight: "bold" }}>
      Widok Kalendarza
    </h3>
    <p style={{ textAlign: "center", color: "var(--text-muted)", marginTop: "0.5rem" }}>
      Tutaj pojawi się pełny, interaktywny kalendarz z datami płatności.
    </p>
    <p style={{ textAlign: "center", marginTop: "1rem", fontWeight: "600" }}>
      Obecnie masz {subscriptions.length} nadchodzących płatności.
    </p>
    <div style={{ marginTop: "2rem", padding: "1rem", background: "var(--table-header-bg)", borderRadius: "8px" }}>
      <h4 style={{ marginBottom: "1rem", fontSize: "1rem", fontWeight: "600" }}>Nadchodzące płatności:</h4>
      {subscriptions.map((sub) => (
        <div key={sub.id} style={{ 
          padding: "0.75rem", 
          marginBottom: "0.5rem", 
          background: "var(--card-bg)", 
          borderRadius: "6px",
          border: "1px solid var(--card-border)",
          display: "flex",
          justifyContent: "space-between",
          alignItems: "center"
        }}>
          <div>
            <span style={{ fontWeight: "600" }}>{sub.name}</span>
            <span style={{ color: "var(--text-muted)", marginLeft: "0.5rem" }}>{sub.nextPayment}</span>
          </div>
          <span style={{ fontWeight: "600", color: "var(--primary)" }}>
            {sub.price.toFixed(2)} {sub.currency}
          </span>
        </div>
      ))}
    </div>
  </div>
);

export default function SubTrackPrototype() {
  const [subscriptions, setSubscriptions] = useState(INITIAL_SUBSCRIPTIONS);
  const [darkMode, setDarkMode] = useState(false);
  const [viewMode, setViewMode] = useState('list'); // 'list' | 'calendar'
  const [newSub, setNewSub] = useState({
    name: "",
    price: "",
    category: "Rozrywka",
    nextPayment: "",
  });

  const [error, setError] = useState("");

  const totalMonthly = useMemo(() => {
    return subscriptions
      .reduce((acc, sub) => acc + parseFloat(sub.price), 0)
      .toFixed(2);
  }, [subscriptions]);

  const chartData = useMemo(() => {
    const data = {};
    subscriptions.forEach((sub) => {
      if (data[sub.category]) data[sub.category] += sub.price;
      else data[sub.category] = sub.price;
    });
    return Object.keys(data).map((key) => ({ name: key, value: data[key] }));
  }, [subscriptions]);

  const handleAddSubscription = (e) => {
    e.preventDefault();

    // Konwersja ceny na liczbę
    const priceValue = parseFloat(newSub.price);

    // Walidacja: Czy pola są wypełnione?
    if (!newSub.name || !newSub.price) {
      setError("Proszę wypełnić nazwę i cenę.");
      return;
    }

    // Walidacja: Czy cena jest poprawna (> 0)?
    if (priceValue <= 0) {
      setError("Cena subskrypcji musi być większa od 0!");
      return;
    }

    // Jeśli wszystko OK, czyścimy błąd i dodajemy
    setError("");

    const newItem = {
      id: Date.now(),
      name: newSub.name,
      price: priceValue, // Używamy sparsowanej liczby
      currency: "PLN",
      category: newSub.category,
      nextPayment: newSub.nextPayment || new Date().toISOString().split("T")[0],
    };

    setSubscriptions([...subscriptions, newItem]);
    setNewSub({ name: "", price: "", category: "Rozrywka", nextPayment: "" });
  };

  const handleDelete = (id) =>
    setSubscriptions(subscriptions.filter((sub) => sub.id !== id));

  return (
    // Główny kontener aplikacji (obsługuje dark mode klasą 'dark-theme')
    <div className={`app-container ${darkMode ? "dark-theme" : ""}`}>
      {/* Nawigacja */}
      <nav className="navbar">
        <div className="logo-section">
          <div className="logo-icon">
            <TrendingUp size={24} color="white" />
          </div>
          <h1>
            Sub<span>Track</span>
          </h1>
        </div>

        <div className="nav-actions">
          <button 
            onClick={() => alert("Przełącz na widok kalendarza!")} 
            className="icon-btn" 
            title="Widok Kalendarza"
          >
            <Calendar size={20} className="calendar-icon" /> 
          </button>
          <button onClick={() => setDarkMode(!darkMode)} className="icon-btn">
            {darkMode ? <Sun size={20} color="#fbbf24" /> : <Moon size={20} />}
          </button>
          <div className="user-avatar">U</div>
        </div>
      </nav>

      <main className="main-content">
        {/* Dashboard - Statystyki */}
        <section className="stats-grid">
          <div className="card stat-card">
            <p className="label">Miesięczne wydatki</p>
            <h2 className="value highlight">{totalMonthly} PLN</h2>
            <p className="subtext">Suma aktywnych subskrypcji</p>
          </div>

          <div className="card stat-card">
            <p className="label">Liczba subskrypcji</p>
            <h2 className="value">{subscriptions.length}</h2>
            <p className="subtext">Aktywne usługi</p>
          </div>

          <div className="card payment-card">
            <div className="payment-header">
              <Calendar size={20} />
              <span>Nadchodząca płatność</span>
            </div>
            <h3>Netflix</h3>
            <p className="big-price">43.00 PLN</p>
            <p className="due-date">Płatność za 3 dni</p>
          </div>
        </section>

        <div className="content-split">
          {/* Lewa kolumna */}
          <div className="left-column">
            {/* Tabela */}
            <div className="card table-card">
              <div className="card-header">
                <LayoutDashboard size={20} />
                <h3>Twoje Subskrypcje</h3>
              </div>
              <div className="table-wrapper">
                <table className="sub-table">
                  <thead>
                    <tr>
                      <th>Usługa</th>
                      <th>Kategoria</th>
                      <th>Data</th>
                      <th className="text-right">Koszt</th>
                      <th className="text-center">Akcja</th>
                    </tr>
                  </thead>
                  <tbody>
                    {subscriptions.map((sub) => (
                      <tr key={sub.id}>
                        <td className="font-bold">{sub.name}</td>
                        <td>
                          <span className="badge">{sub.category}</span>
                        </td>
                        <td className="text-muted">{sub.nextPayment}</td>
                        <td className="text-right font-bold">
                          {sub.price.toFixed(2)} {sub.currency}
                        </td>
                        <td className="text-center">
                          <button
                            onClick={() => handleDelete(sub.id)}
                            className="btn-delete"
                          >
                            <Trash2 size={16} />
                          </button>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </div>

            {/* Formularz */}
            <div className="card form-card">
              <div className="card-header">
                <PlusCircle size={20} color="#10b981" />
                <h3>Dodaj subskrypcję</h3>
              </div>

              <form onSubmit={handleAddSubscription} className="add-form">
                {/* 3. NOWE: Wyświetlanie błędu, jeśli istnieje */}
                {error && <div className="error-message">{error}</div>}

                <input
                  type="text"
                  placeholder="Nazwa (np. HBO Max)"
                  value={newSub.name}
                  onChange={(e) =>
                    setNewSub({ ...newSub, name: e.target.value })
                  }
                  // Usunąłem 'required', bo teraz walidujemy ręcznie w JS
                />
                <input
                  type="number"
                  placeholder="Cena (PLN)"
                  value={newSub.price}
                  onChange={(e) =>
                    setNewSub({ ...newSub, price: e.target.value })
                  }
                  step="0.01"
                  min="0.01"
                />
                <select
                  value={newSub.category}
                  onChange={(e) =>
                    setNewSub({ ...newSub, category: e.target.value })
                  }
                >
                  <option>Rozrywka</option>
                  <option>Praca</option>
                  <option>Zdrowie</option>
                  <option>Edukacja</option>
                  <option>Inne</option>
                </select>
                <input
                  type="date"
                  value={newSub.nextPayment}
                  onChange={(e) =>
                    setNewSub({ ...newSub, nextPayment: e.target.value })
                  }
                />
                <button type="submit" className="btn-primary">
                  Dodaj do listy
                </button>
              </form>
            </div>
          </div>

          {/* Prawa kolumna */}
          <div className="right-column">
            {/* Wykres */}
            <div className="card chart-card">
              <h3>Struktura wydatków</h3>
              <div className="chart-wrapper">
                <ResponsiveContainer width="100%" height="100%">
                  <PieChart>
                    <Pie
                      data={chartData}
                      cx="50%"
                      cy="50%"
                      innerRadius={60}
                      outerRadius={80}
                      paddingAngle={5}
                      dataKey="value"
                    >
                      {chartData.map((entry, index) => (
                        <Cell
                          key={`cell-${index}`}
                          fill={COLORS[index % COLORS.length]}
                        />
                      ))}
                    </Pie>
                    <RechartsTooltip />
                    <Legend verticalAlign="bottom" height={36} />
                  </PieChart>
                </ResponsiveContainer>
              </div>
            </div>

            {/* Ustawienia */}
            <div className="card settings-card">
              <div className="card-header">
                <Settings size={20} className="text-muted" />
                <h3>Szybkie ustawienia</h3>
              </div>
              <div className="setting-row">
                <span>Waluta domyślna</span>
                <span className="highlight">PLN</span>
              </div>
              <div className="setting-row">
                <span>Powiadomienia e-mail</span>
                <div className="toggle-switch active"></div>
              </div>
            </div>
          </div>
        </div>
      </main>
    </div>
  );
}
